MapColorsAttrib.vertexProgram:
attribute float dataAttrib;
varying float data;

void main (void)
{
    data = dataAttrib;

    // Do fixed functionality vertex transform
    gl_Position = ftransform();
#ifdef __GLSL_CG_DATA_TYPES
    gl_ClipVertex = gl_ModelViewMatrix*gl_Vertex;
#endif
}

MapColorsAttrib.fragmentProgram:
uniform sampler1D texUnit1;
uniform float rangeMin, rangeMax;

varying float data;

void main (void) 
{
    float t = (data-rangeMin)/(rangeMax-rangeMin);
    vec4 color = texture1D(texUnit1, t);
    gl_FragColor = color;
}
